<template>    <div>        <div class="msg_header">            {{item.name}}        </div>        <ul class="as_msg">            <li  v-for="it in item.text">                <p>{{it}}</p>            </li>        </ul>        <ul v-if="self" class="self_msg">            <li v-for="ml in self">              <p>{{ml}}</p>            </li>        </ul>        <div class="btn_send">            <input type="text" v-model="inputText">            <button @click="send">发送</button>        </div>    </div></template><script>export  default{    props : ['item','self'],    data : function(){        return {            inputText : ''        }    },    methods:{        send:function(){            this.$emit('sendMessage',{                inputText : this.inputText            })        }    }}</script><style lang="scss">.msg_header{    text-align: center;}    .self_msg{        float: right;    }    .btn_send{        clear: both;    }    ul.as_msg p{        width: 100px;    }ul.self_msg p {    width: 100px;    word-wrap: break-word;    text-align: right;}</style>