<template>    <div>        <message-info :self="msg" :item="asItem"    @sendMessage="receive"></message-info>    </div></template><script>    import MessageInfo from '../components/MessageInfo.vue'export default{        created : function(){            this.initAsMessage();        } ,        data : function(){            return {                msg : []            }    },     computed : {        asItem : function(){             console.log('as');            return this.$store.getters.getAsMessage        },         getId : function(){             var id = this.$route.query.id;             return id;         }     },     components : {         "message-info" : MessageInfo     },    methods:{            initAsMessage : function(){            this.$http.get('src/static/asMessage.json').then(function(res){                for(var i =0;i<res.data.length;i++){                    if(this.getId == res.data[i].id){                        this.$store.commit('setAsMessage',res.data[i])                    }                }            },function(res){            })        } ,        receive: function(opt){                this.msg.push(opt.inputText);        }    }}</script><style></style>